Ext.define('SenchaDash.view.email.InboxPanelViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.emailinboxpanel',

    afterInboxPanelRender: function(component, eOpts) {
        var pagingToolbar = this.getView().query('#pagingToolbar')[0];

        /*setTimeout(function(){
            pagingToolbar.getStore().load();
        },150);*/

        //pagingToolbar.relayEvents(pagingToolbar.getStore(), ['load']);
    },
    onGridCellItemClick: function(_this, td, cellIndex, record, tr, rowIndex, e, eOpts ){
        if(cellIndex != 0 && cellIndex != 1){
            var content = record.get('contents');
            var emailMainViewController = SenchaDash.app.getController('Navigation').getEmailMainContainer().getController();
            emailMainViewController.setCurrentView('SenchaDash.view.email.SingleMailContainer', {"record":record});    
        }else if(cellIndex==1){
         var classname = td.children[0].children[0].className.toString();
         if(classname.indexOf("fa-heart-o")!=-1){
             td.children[0].children[0].className="fa fa-heart"
         }else{
             td.children[0].children[0].className="fa fa-heart-o"
         }
        }
    }
});
